mapper.select:
select 

from b_creditor_rights
where 1 = 1

	and creditor_rights_type = #{creditorRightsType, jdbcType=INTEGER}


	and match_status = #{matchStatus, jdbcType=INTEGER}


	and collect_status = #{collectStatus, jdbcType=INTEGER}

limit #{startRow, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}

	select count(*) from b_creditor_rights where 1 = 1

  	and creditor_rights_type = #{creditorRightsType, jdbcType=INTEGER}


  	and match_status = #{matchStatus, jdbcType=INTEGER}


  	and collect_status = #{collectStatus, jdbcType=INTEGER}

select 

from b_creditor_rights
where 1 = 1

	and match_status = #{matchStatus, jdbcType=INTEGER}


	and collect_status = #{collectStatus, jdbcType=INTEGER}


	and id = #{creditorRightsId, jdbcType=INTEGER}


	and (loan_contract_path is null or loan_contract_path = '')

SELECT r.id, r.apply_no, r.apply_purpose, r.audit_loan_money, r.audit_loan_term, r.borrower_realname, r.borrower_idcard, case when r.borrower_sex=1 then '男' when r.borrower_sex=2 then '女' else '未知' end borrower_sex, r.borrower_present_address, r.collect_finish_time, bid.id bidId, bid.bidMoney, u.id userId, u.phone, u.name, u.idCard FROM p2padmin.b_creditor_rights r LEFT JOIN p2padmin.b_loan_creditor_match m ON r.id = m.creditor_id LEFT JOIN p2p2.b_loan_info loan ON m.loan_id = loan.id LEFT JOIN p2p2.b_bid_info bid on loan.id = bid.loanId LEFT JOIN p2p2.u_user_info u ON bid.uid = u.id WHERE r.match_status = 1 and r.collect_status = 1
and r.id = #{creditorId, jdbcType=INTEGER}
and (loan_contract_path is null or loan_contract_path = '')
